import React, { useState } from "react";
import "./App.css";
import Search from "./components/search/Search";
import axios from "axios";
import cors from "cors";

const App = () => {
  const [temp, setTemp] = useState([]);

  const [city, setCity] = useState(null);

  const searchCity = async () => {
    await fetch(
      `https://api.weatherbit.io/v2.0/forecast/daily?city=${city}&key=46393c9cae6042c89b25a9d168fd6532`
    )
    .then(r => console.log(r.json()))
      .then((r) => r.json())
//       .then((recurso) => {
//         console.log(recurso);
//       });
  };
  const onChange = e => setCity({ [e.target.name]: e.target.value })

  return (
    <div>
      {/* <Search city={city} searchCity={searchCity} /> */}
      <form onSubmit={searchCity} className="formSearchCity">
                                <input
                                        type="text"
                                        name="city"
                                        id="inputSearchCity"
                                        onChange={onChange}
                                        placeholder="Search City..."
                                />
                                <button type="submit" className="btn btn-outline-info text-white">
                                        Search
                                </button>
                        </form>
    </div>
  );
};

export default App;
