import React, { useState } from "react";
import "./App.css";
import Search from "./components/search/Search";
import axios from "axios";
import cors from 'cors'

const App = () => {

  const [temp, setTemp] = useState([]);

  const [city, setCity] = useState(null);

  const searchCity = () => {
    var config = {
      withCredentials: true,
      method: "GET",
      url: `http://api.weatherbit.io/v2.0/forecastdaily?city=${city}&key=46393c9cae6042c89b25a9d168fd6532`,
      mode: 'no-cors',
      headers: {
        // 'Access-Control-Allow-Origin': 'http://localhost:3000',
        'Content-Type': 'application/json',
      },
      credentials: 'same-origin',
    };
    axios(config).then((res) => {
      const temp = res.data.data;
      const city = res.data.city_name;
      console.log("VERR VALOR TEMP", temp);
      console.log("VERR VALOR city", city);
    });
  };

  return (
    <div>
      <Search city={city} searchCity={searchCity} />
    </div>
  );
};

export default App;
